import React, { Component } from 'react';
import logo from './logo.svg';
import Map from './containers/MapContainer';
import LeftSearchBar from './components/LeftSearchBar';
import firebase from './firebase';

// CSS
import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import 'antd/dist/antd.css';
// import 'https://www.gstatic.com/firebasejs/7.7.0/firebase-app.js';
// import 'https://www.gstatic.com/firebasejs/7.7.0/firebase-analytics.js';
// import 'https://www.gstatic.com/firebasejs/7.7.0/firebase-auth.js';
// import 'https://www.gstatic.com/firebasejs/7.7.0/firebase-database.js';

class App extends Component {
  state = {
    qualifiedPostLocationX : [],
    qualifiedPostLocationY : [],
    searchValue : ""
  }

  componentDidMount(){
    if (this.state.searchValue != ""){
      const ref = firebase.database().ref().child('/Users').on('value', async function(snap){
        posts = await snap.val();
        for (var i = 0; i < Object.keys(posts).length; ++i){
          var cur = posts[Object.keys(posts)[i]];
          var keys = Object.keys(cur);
          for (var j = 0 ; j < keys.length; j++){
            var str = cur[keys[j]];
            if (typeof str == "string" && str.includes(value)){
              states.qualifiedPostLocationX.push(cur['x']);
              states.qualifiedPostLocationY.push(cur['y']);
              console.log(states.qualifiedPostLocationX);
            }
          }
        };
      });
      this.setState({
        "qualifiedPostLocationX": states.qualifiedPostLocationX,
        "qualifiedPostLocationY": states.qualifiedPostLocationY
      });
    }
  }

  searchCourse = async value => {
    if (value != null){
      this.setState({ "searchValue": value});
    }
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          {/* <img src={logo} className="App-logo" alt="logo" /> */}
        </header>
        <div>
          <LeftSearchBar 
            onSearchClick={this.searchCourse}
          />
          <Map
            center={{ lat: 40.64, lng: -73.96 }}
            zoom={12}
            markerLocX={this.state.qualifiedPostLocationX}
            markerLocY={this.state.qualifiedPostLocationY}
            googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyBrR5fLDg5k8V4lK9XpxIUnRrhzBrcuoPQ"
            loadingElement={<div style={{ height: '100%'}} />}
            containerElement={<div style={{ height: '800px' }} />}
            mapElement={<div style={{ height: '100%' }} />}
          />
        </div>
      </div>
    );
  }
}

export default App;
